// SPDX-License-Identifier: agpl-3.0
pragma solidity ^0.8.0;

import "@chainlink/contracts/src/v0.8/interfaces/AggregatorV3Interface.sol";

contract MockChainlinkOracleFeed is AggregatorV3Interface {
    int256 price;
    uint8 numDecimals;
    uint256 age;

    constructor(int256 _price, uint8 _decimals) {
        price = _price;
        numDecimals = _decimals;
        age = 30;
    }

    function decimals() external view override returns (uint8) {
        return numDecimals;
    }

    function description() external pure override returns (string memory) {
        return "MockOracleFeed";
    }

    function version() external pure override returns (uint256) {
        return 1;
    }

    function setPrice(int256 _price) public {
        price = _price;
    }

    function setDecimals(uint8 _decimals) public {
        numDecimals = _decimals;
    }

    function setAge(uint256 _age) public {
        age = _age;
    }

    // getRoundData and latestRoundData should both raise "No data present"
    // if they do not have data to report, instead of returning unset values
    // which could be misinterpreted as actual reported values.
    function getRoundData(uint80 _roundId)
    external
    view
    override
    returns (
        uint80 roundId,
        int256 answer,
        uint256 startedAt,
        uint256 updatedAt,
        uint80 answeredInRound
    )
    {
        roundId = _roundId;
        answer = price;
        startedAt = block.timestamp - age;
        updatedAt = block.timestamp - age;
        answeredInRound = 0;
    }

    function latestRoundData()
    external
    view
    override
    returns (
        uint80 roundId,
        int256 answer,
        uint256 startedAt,
        uint256 updatedAt,
        uint80 answeredInRound
    )
    {
        roundId = 0;
        answer = price;
        startedAt = block.timestamp - age;
        updatedAt = block.timestamp - age;
        answeredInRound = 0;
    }
}

contracts/mocks/MockUniswapRouter.sol.content:
// SPDX-License-Identifier: agpl-3.0
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";

contract MockUniswapRouter {

    uint256 outputBps;

    function initialize() public {
        outputBps = 10000; // by default output the same as the input
    }

    function swapExactTokensForTokens(
        uint256 amountIn,
        uint256 amountOutMin,
        address[] calldata path,
        address to,
        uint256 deadline
    ) external returns (uint256[] memory) {
        address tok0 = path[0];
        address tok1 = path[1];
        // Give based on outputBps
        uint256 amountOut = _scaleBy(amountIn, IERC20Metadata(tok1).decimals(), IERC20Metadata(tok0).decimals());
        amountOut = amountOut * outputBps / 10000;

        require(amountOut >= amountOutMin, "Slippage error");
        require(deadline > block.timestamp);

        IERC20Metadata(tok0).transferFrom(msg.sender, address(this), amountIn);
        IERC20Metadata(tok1).transfer(to, amountOut);

        uint256[] memory amounts = new uint256[](2);
        amounts[0] = amountIn;
        amounts[1] = amountOut;

        return amounts;
    }

    function getAmountsOut(
        uint256 amountIn,
        address[] memory path
    ) external view returns (uint256[] memory) {
        address tok0 = path[0];
        address tok1 = path[1];
        // Give based on outputBps
        uint256 amountOut = _scaleBy(amountIn, IERC20Metadata(tok1).decimals(), IERC20Metadata(tok0).decimals());
        amountOut = amountOut * outputBps / 10000;

        uint256[] memory amounts = new uint256[](2);
        amounts[0] = amountIn;
        amounts[1] = amountOut;

        return amounts;
    }

    function setOutputBps(
        uint256 _outputBps
    ) external {
        outputBps = _outputBps;
    }

    function _scaleBy(
        uint256 x,
        uint256 to,
        uint256 from
    ) internal pure returns (uint256) {
        if (to > from) {
            return x * (10**(to - from));
        } else if (to < from) {
            return x / (10**(from - to));
        } else {
            return x;
        }
    }
}

